            elems += vtos(a[i]) + " ";
        elems += "... ";
        forn(i, 2)
            elems += vtos(a[a.size() - 2 + i]) + " ";
    }

    return format("\"%s\" [size=%u]", trim(elems).c_str(), (unsigned int)(a.size()));
